// Pick 6 random numbers from 1 to 50// Implemented (c) 1997 William Lenhartimport java.util.Random;public class LottoHelper {	// Note this method may print duplicate numbers	public static void main(String args[]) {		// Create a random number generator		Random picker = new Random();				int nextNum;	// Number btn 1 and 50 to be chosen		System.out.println();		// skipline		for(int i = 0; i < 5; i++) {			// nextInt(50) gives values between 0 and 49			// Add 1 to get btn 1 and 50			nextNum = picker.nextInt(50) + 1;			System.out.print(nextNum + ", ");		}		nextNum = picker.nextInt(50) + 1;		System.out.println(nextNum);		System.out.println();		// skipline	}}